package com.singe.common.interceptor;import com.singe.common.utils.ConstantConfig;import org.springframework.web.servlet.handler.HandlerInterceptorAdapter;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;public class AuthInterceptor extends HandlerInterceptorAdapter {    /**     * 创建一个新的实例 AuthInterceptor.     *     */    public AuthInterceptor() {    }    @Override    public boolean preHandle(HttpServletRequest request, HttpServletResponse response, Object handler) throws Exception {        Integer uid = (Integer) request.getSession().getAttribute(ConstantConfig.UID);        if (null == uid) {            response.sendRedirect(request.getContextPath() + "/login");            return false;        }else{            return true;        }    }}